import React, { useState, useEffect, useContext } from 'react'
import { Link } from 'react-router-dom'
import ListElement from '../UI/ListElement/ListElement';
import DeleteButton from '../UI/DeleteButton/DeletteButton';
import Login from '../pages/Login/Login';
import CreateButton from '../UI/CreateButton/CreateButton';
import SetLoginToken from '../pages/Login/LoginService';
import AuthContext from './Context/AuthContext';

export default function Header() {
    console.log( useContext(AuthContext))
    const [login, setlogin] = useState();
    const [tokens, setToken] = useState();
    const [username, setusername] = useState(localStorage.getItem('user'))
    function change_color() {
        var el = document.querySelector('*');
        if (el.style['filter'] == 'invert(1)') {
            el.style = 'filter:invert(0)'
        }
        else
            el.style = 'filter:invert(1)'
    }
    useEffect(() => {
        const user = localStorage.getItem('user') || null
        const tokens_ = JSON.parse(localStorage.getItem('authTokens')) || null
        setlogin(user)
        setToken(tokens_)
    }, [username]);
    useEffect(() => {
        SetLoginToken(tokens, login)
    }, [login, username]);
    const [visibility, setVisibility] = useState(false);

    return (
        <header>
            <div>

                <Login visible={visibility} setVisible={setVisibility}></Login>
                <Link className='logotype' to={'/about'} />
                <span className='logo'>Note web service</span>
                <ul className='nav'>
                    <ListElement link={'/about'} >About</ListElement>
                    <ListElement link={'/birthdays'} >Check birthdays</ListElement>
                    <ListElement link={'/notes'} >Check my notes</ListElement>
                    <ListElement link={'/create_category'} >Create Category</ListElement>
                    {username ?
                        <ListElement onClick={SetLoginToken(null, null)} >{login} Logout</ListElement>
                        :
                        <ListElement onClick={() => setVisibility(true)} >Login</ListElement>
                    }
                    <h1>{name}</h1>
                </ul>
            </div>
            <DeleteButton onClick={change_color} >
                switch the light
            </DeleteButton>
            <div className='presentation'>

            </div>

        </header>
    )
}
