import { useContext } from "react"
import AuthContext from "../../components/Context/AuthContext"
export default async function GetAllNotes() {
    const { authTokens } = useContext(AuthContext)
    try {
        const response = await fetch('api/get_notes', {
            headers: {
                'Authorization': `Bearer ${authTokens.access}`
            }
        }
        )

        return response.json()
    } catch (error) {
    }
}

export const createNote = async (access, body, cat_id, userId) => {
    let response = await fetch(`/api/create_note/`, {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${access}`
        },
        body: JSON.stringify({
            'body': body,
            'category': cat_id,
            'user': userId,
        })
    })
    return response
}

export const getNote = async (access, id) => {
    let response = await fetch(`/api/get_note/${id}`,
        {
            headers: {
                'Authorization': `Bearer ${access}`
            }
        })
    return response
}


export const updateNote = async (access,id, body) => {
    await fetch(`/api/update_note/${id}`, {
        method: 'PATCH',
        headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${access}`
        },
        body: JSON.stringify({ 'body': body })
    })
}
