import React, { useEffect, useState } from 'react'
import { useParams, Link } from 'react-router-dom';

export const NotePage = () => {
    let { id } = useParams();
    const [note, setNote] = useState(null);

    useEffect(() => {
        get_note()
    }, [id])

    let get_note = async () => {
        let response = await fetch(`/api/get_note/${id}`)
        let data = await response.json()
        setNote(data)
    }
    let update_note = async () => {
        fetch(`/api/get_note/${id}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(note)


        })
    }
    let handle_submit = () => {
        update_note()
    }
    let Change
    return (
        <div>
            <Link onClick={handle_submit}>
                Save
            </Link>
            <br/>
            <textarea className='change_text_area' defaultValue={note?.body} onChange={(event) => { setNote({ ...note, 'body': event.target.value }) }}></textarea>

        </div>
    )
}
