import React, { useEffect, useState } from 'react'
import { useParams, Link, useNavigate } from 'react-router-dom';

import classes from './CreateCategory.module.css'
import CreateButton from '../../UI/CreateButton/CreateButton';
import ColorPicker from '../../UI/ColorPicker/ColorPicker';
import InputField from '../../UI/InputField/InputField';


const CreateCategory = ({ children, onChange, ...props }) => {

    const [category, setCategory] = useState({ 'color': null, 'name': null });
    const [errorplaceholder, errorsetplaceholder] = useState('Category name');
    const navigate = useNavigate();

    const Create_Category = async () => {
        if (category.color == null) {
            alert('Define Color')
            return
        }
        if (category.name == null) {
            // console.log(document.getElementsByName('note_cat')[1]);
            errorsetplaceholder('type name!')
            // document.getElementsByName('note_cat')[1].placeholder = "shouldn't be empty";
            return
        }
        console.log(category);
        let response = await fetch(`/api/create_cat`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(category)
        })
        if (response.status === 201) {
            navigate('/notes')
        }
    }
    return (
        <div>
            <ColorPicker onChange={e => setCategory({ ...category, 'color': e.target.value })} />
            <InputField name='note-cat' clss={[classes.cat_input]} placeholder={errorplaceholder} onChange={e => setCategory({ ...category, 'name': e.target.value })}></InputField>

            <CreateButton onClick={Create_Category}>Create category</CreateButton>
        </div>
    )
}

export default CreateCategory
