import React, { useEffect, useState } from 'react'
import { useParams, Link, useNavigate } from 'react-router-dom';
import ColorPicker from '../../UI/ColorPicker/ColorPicker';
import { useFetching } from '../../components/hooks/useFetchingNotes';
import CatsService from '../../API/Cats/CatsService';
import CatSelector from '../../UI/Selector/CatSelector';
export const NotePage = () => {
    let { id } = useParams();
    const [note, setNote] = useState(null);
    const [color, setColor] = useState('');

    const navigate = useNavigate();
    useEffect(() => {
        get_note()

    }, [id])

    useEffect(() => {
        update_note()
        fetchCats()
    }, [note]);
    let get_note = async () => {
        let response = await fetch(`/api/get_note/${id}`)
        if (response.status === 200) {
            setNote(await response.json())
            return
        }
        navigate('/all')
    }
    let update_note = async () => {
        fetch(`/api/update_note/${id}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(note)


        })
    }
    function ChangeNote(event) {
        setNote({ ...note, 'body': event.target.value })

        update_note()
    }
    async function delete_note() {
        fetch(`/api/delete_note/${id}`, {
            method: 'DELETE',
            headers: {
                'Content-Type': 'application/json'
            }
        })
    }
    const [cats, setCats] = useState([]);
    const [fetchCats, isLoading, error] = useFetching(async () =>{
        let fetched_cats = await CatsService.get_all_cats()
        setCats(fetched_cats)
    })
    function ChangeCat(item){
        // setSelectedSort(sort)
        console.log('pizda');
        console.log(selectedElement);
        // setPosts(sortedPosts)
      }
    const [selectedElement, setSelectedElement] = useState()
    return (
        <div>
            <Link onClick={(event) => ChangeNote(event)} className='backarrow' to={'/notes'}>
                <h1 >‚Üê</h1>
            </Link>
            <Link onClick={delete_note} className='delete' to={'/notes'}>
                <h1 >delete</h1>
            </Link>

            <textarea className='change_text_area' defaultValue={note?.body} onChange={(event) => ChangeNote(event)}></textarea>
            <hr />
            <CatSelector value={selectedElement} handleChange={ChangeCat(this)} className='color_picker' cats={cats} />
        </div>
    )
}
