import React, { useEffect, useState } from 'react'
import { useParams, Link, useNavigate } from 'react-router-dom';
export const BirthdayPage = () => {
    let { id } = useParams();
    const [description, setDescription] = useState(null);
    const [birthday, setBirthday] = useState(null);
    const navigate = useNavigate();
    useEffect(() => {
        get_birthday()
    }, [id])
    let get_birthday = async () => {
        let response = await fetch(`/api/get_birthday/${id}`)
        if (response.status === 200) {
            setDescription(await response.json())
            setBirthday(await response.json())
            return
        }
        navigate('/about')
    }
    function ChangeDescription(event) {
        setDescription({ ...birthday, 'description': event.target.value })
        update_item()
    }
    function ChangeBirthday(event) {
        setBirthday({ ...birthday, 'description': event.target.value })
        update_item()
    }
    let update_item = async () => {
        fetch(`/api/update_birthday/${id}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(birthday)
        })
    }
    return (
        <div>
            <textarea className='change_text_area' defaultValue={birthday?.description} onChange={(event) => ChangeDescription(event)}></textarea>
            <input type="date" onChange={(event) => ChangeBirthday(event)} value={birthday?.birthday}></input>
        </div>
    )
}
