export default class NoteService {
    static async get_all_notes() {
        try {
            const tokens = JSON.parse(localStorage.getItem('authTokens'))
            let refresh_response = await fetch(`/api/token/refresh/`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ 'refresh': tokens.refresh })
            })
                let new_tokens = await refresh_response.json()
                localStorage.setItem('authTokens', JSON.stringify({'access':new_tokens.access,'refresh':tokens.refresh}))

            const response = await fetch('api/get_notes', {
                headers: {
                    'Authorization': `Bearer ${tokens?.access}`
                }
            }
            )

            return response.json()
        } catch (error) {
        }

    }
}
