import React, { useEffect, useState } from 'react'
import { useParams, Link, useNavigate } from 'react-router-dom';

import classes from './CreateCategory.module.css'
import CreateButton from '../../UI/CreateButton/CreateButton';


const CreateCategory = ({ children, onChange, ...props }) => {

    const [category, setCategory] = useState(null);
    useEffect(() => {
    }, [category])
    const navigate = useNavigate();

    let change_color = (e) => {
        setCategory({ ...category, 'color': e.target.value })
    }
    let change_name = (e) => {
        setCategory({ ...category, 'name': e.target.value })
    }

    const Create_Category = async () => {


        let response = await fetch(`/api/create_cat/`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(category)
        })
        if (response.status === 200) {
            navigate('/notes')
        }
        document.getElementsByName('note_cat')[0].placeholder = "shouldn't be empty";

    }
    return (
        <div>
            <input
                className={classes.cat_input}
                placeholder='Category name'
                type='text'
                name='note_cat'
                onChange={e => change_name(e)} >
            </input>
            <input {...props}
                onChange={e => change_color(e)}
                className={classes.myColorPicker}
                type="color" ></input>
            <CreateButton onClick={Create_Category}>Create category</CreateButton>
        </div>
    )
}

export default CreateCategory
