import React, { useEffect, useState } from 'react'
import { useParams, Link, useNavigate } from 'react-router-dom';
export const BirthdayPage = () => {
    let { id } = useParams();
    const [birthday, setBirthday] = useState(null);
    const navigate = useNavigate();
    useEffect(() => {
        get_note()
    }, [id])
    let get_birthday = async () => {
        let response = await fetch(`/api/get_birthday/${id}`)
        if (response.status === 200) {
            setBirthday(await response.json())
            return
        }
        navigate('/all')
    }
    function ChangeBirthday(event) {
        setBirthday({ ...note, 'body': event.target.value })

        update_note()
    }
    let update_note = async () => {
        fetch(`/api/update_birthday/${id}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(birthday)
        })
    }
    return (
        <div>
            <textarea className='change_text_area' defaultValue={birthday?.description} onChange={(event) => ChangeBirthday(event)}></textarea>
        </div>
    )
}
