import ColorPicker from '../ColorPicker/ColorPicker'
import classes from './CatSelector.module.css'
import React, { useEffect, useState } from 'react'
import { useFetching } from '../../components/hooks/useFetchingNotes';
import CatsService from '../../API/Cats/CatsService';

const CatSelector = ({note,value, ...props  }) => {
    const [selectedCat, setselectedCat] = useState('');
    const [cats, setCats] = useState([]);
    const [fetchCats, isLoading, error] = useFetching(async () => {
        let fetched_cats = await CatsService.get_all_cats()
        setCats(fetched_cats)
    })
    useEffect(() => {
        fetchCats()
    }, [cats])

    useEffect(() => {
        ChangeCat()
    }, [selectedCat])

    const ChangeCat = (selected_value) =>{
        setselectedCat(selected_value)
        if (selectedCat !=null){    
        console.log(selectedCat);

            // note?.category.name = selectedCat
            // fetch(`/api/update_note/${note.id}`, {
            //     method: 'PUT',
            //     headers: {
            //         'Content-Type': 'application/json'
            //     },
            //     body: JSON.stringify(note)


            // })
        }

    }
    return (
        <select {...props} defaultValue={note?.category.name} value={selectedCat}  onChange={e => ChangeCat(e.target.value)}>
            {cats.map(cat =>
                <option key={cat.id} value={cat.name} >
                    {cat.name}
                </option>
            )}
        </select>

    )
}

export default CatSelector
