import { createContext, useState, useEffect } from "react";
import jwt_decode from "jwt-decode";
const AuthContext = createContext()
export default AuthContext

export const AuthProvider = ({ children }) => {

    const [user, setUser] = useState(() => localStorage.getItem('authTokens') ? JSON.parse(localStorage.getItem('authTokens')).username : null)
    const [authTokens, setauthTokens] = useState(() => localStorage.getItem('authTokens') ? jwt_decode(localStorage.getItem('authTokens')) : null)

    let loginUser = async (username, password) => {
        let response = await fetch(`/api/token/`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ 'username': username, 'password': password })
        })
        let data = await response.json()
        console.log(data);
        if (response.status === 200) {
            setauthTokens(data)
            console.log(data.access)
            console.log(jwt_decode(data.access));
            setUser(jwt_decode(data.access))
            // localStorage.setItem('authTokens', JSON.stringify(data))

            // setauthTokens(data)
            // console.log(jwt_decode(data.access));
            // setUser(jwt_decode(data.access))
            // localStorage.setItem('authTokens', JSON.stringify(data))
        }
        else {
            alert('bad data')
        }
    }
    let contextData = {
        user: user,
        loginUser: loginUser
    }

    return (
        <AuthContext.Provider value={contextData}>
            {children}
        </AuthContext.Provider>
    )
}
